import React, { useContext, useState } from 'react';
import { AppContext } from '../context/AppContext';

const Budget = () => {
  const { budget, dispatch, currency } = useContext(AppContext);
  const [cost, setCost] = useState(budget);

  const handleBudgetChange = (event) => {
    const newBudget = parseInt(event.target.value, 10);

    // Check if the new budget exceeds the limit (2000 in this case)
    if (newBudget > 20000) {
      alert('Budget cannot exceed 2000.');
      return;
    }

    // Update the budget in the context
    dispatch({
      type: 'SET_BUDGET',
      payload: newBudget,
    });

    // Update the local state
    setCost(newBudget);
  };

  return (
    <div className='alert alert-secondary'>
      <span>
        Budget: {currency}{' '}
        <input
          type="number"
          value={cost}
          onChange={handleBudgetChange}
        />
      </span>
    </div>
  );
};

export default Budget;
